pragma solidity >=0.4.22 <0.9.0;

contract Election {
    uint public electionCount = 0;

    struct ElectionDetail {
        uint id;
        string election_title;
        bool completed;
    }

    mapping(uint => ElectionDetail) public elections;


    constructor() public{
     createElection("CUSAT COLLEGE ELECTION");   

    }

    function createElection(string memory _title ) public{
        electionCount++;
